mapper.select:
select a.seq ,a.cg_Name ,a.cg_UseNY ,a.cg_Order ,a.cg_Desc ,a.cg_ReDate ,a.cg_MoDate ,a.cg_DelNY ,(select count(b.CodeGroup_seq) from code b where 1 = 1 and b.CodeGroup_seq = a.seq) as codeGroupCount from CodeGroup a where 1 = 1 And cg_DelNY = #{sh_DelNY} And cg_UseNY = #{sh_UseNY}
AND a.cg_ReDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd} AND a.cg_MoDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd}
AND a.seq = #{sh_Value} AND cg_Name LIKE CONCAT('%',#{sh_Value},'%') AND cg_Order = #{sh_Value}
order by a.seq desc

mapper.update:
update CodeGroup
set
	cg_Name = #{cg_Name}
	,cg_UseNY = #{cg_UseNY}
	,cg_Order = #{cg_Order}
	,cg_Desc = #{cg_Desc}
	,cg_DelNY = #{cg_DelNY}
where 1 = 1
	and seq = #{seq}

mapper.delete:
delete from codegroup
where 1=1
	and seq = #{seq}
