mapper.sql:
from
	CodeGroup a
where 1 = 1
And cg_DelNY = #{sh_DelNY}
And cg_UseNY = #{sh_UseNY}


	AND a.cg_ReDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd}
	AND a.cg_MoDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd}



	AND a.cgseq = #{sh_Value} 
	AND cg_Name LIKE CONCAT('%',#{sh_Value},'%') AND cg_Name = #{sh_Value}
	AND cg_Order = #{sh_Value}

mapper.update:
update CodeGroup
set
	cg_Name = #{cg_Name}
	,cg_UseNY = #{cg_UseNY}
	,cg_Order = #{cg_Order}
	,cg_Desc = #{cg_Desc}
	,cg_DelNY = #{cg_DelNY}
where 1 = 1
	and cgseq = #{cgseq}

mapper.delete:
delete from codegroup
where 1=1
	and cgseq = #{cgseq}
