mapper.sql:
from
	Code a
left join
	codegroup b
on
	a.CodeGroup_seq = b.cgseq
WHERE 1=1		
And cd_DelNY = #{sh_DelNY}
And cd_UseNY = #{sh_UseNY}


	AND CodeGroup_seq = #{sh_Value}
	AND cg_Name LIKE CONCAT('%',#{sh_Value},'%')
	AND a.cdseq = #{sh_Value} 
	AND cd_Name LIKE CONCAT('%',#{sh_Value},'%') 



	AND a.cd_ReDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd}
	AND a.cd_MoDate BETWEEN #{sh_DateStart} AND #{sh_DateEnd}

mapper.update:
update Code
set
	cd_Name = #{cd_Name}
	,cd_UseNY = #{cd_UseNY}
	,cd_Order = #{cd_Order}
	,cd_Desc = #{cd_Desc}
	,cd_DelNY = #{cd_DelNY}
	,CodeGroup_seq = #{CodeGroup_seq}
where 1 = 1
	and cdseq = #{cdseq}

mapper.delete:
delete from code
where 1=1
	and cdseq = #{cdseq}
