mapper.sql:
from
	member a
where 1 = 1
AND memDelNY = #{shDelNY}
AND memGender = #{shGender}	


	AND a.memseq = #{shValue}
	AND a.memName LIKE CONCAT('%',#{shValue},'%')
	AND a.memId LIKE CONCAT('%',#{shValue},'%')
	AND a.memEmail LIKE CONCAT('%',#{shValue},'%')
	AND a.memPhoneNum LIKE CONCAT('%',#{shValue},'%')



	AND a.memReDate BETWEEN #{shStartDate} AND #{shEndDate}
	AND a.memMoDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
		insert into member(
    		memName
    		,memGender
    		,memId
    		,memPassword
            ,memEmail
            ,memPhoneNum
            ,memReDate
            ,memMoDate
    		,memDelNY
		)values(
    		#{memName}
    		,#{memGender}
    		,#{memId}
    		,#{memPassword}
            ,#{memEmail}
            ,#{memPhoneNum}
            ,now()
    		,now()
    		,0
		)

			SELECT last_Insert_id()

mapper.update:
update member
set
	memName = #{memName}
	,memGender = #{memGender}
	,memId = #{memId}
	,memPassword = #{memPassword}
	,memEmail = #{memEmail}
	,memPhoneNum = #{memPhoneNum}
	,memDelNY = #{memDelNY}
where 1 = 1
	and memseq = #{memseq}

mapper.delete:
delete from member
where 1=1
	and memseq = #{memseq}
