mapper.select:
select a.prseq ,a.prProdName ,a.prUseNY ,a.prProdDesc ,a.prReguPrice ,a.prSalePrice ,a.prInventory ,a.prReDate ,a.prMoDate ,a.prDelNY from product a where 1 = 1 AND prDelNY = #{shDelNY} AND prUseNY = #{shUseNY}
AND a.prseq = #{shValue} AND a.prProdName LIKE CONCAT('%',#{shValue},'%') AND a.prReguPrice LIKE CONCAT('%',#{shValue},'%') AND a.prSalePrice LIKE CONCAT('%',#{shValue},'%') AND a.prInventory LIKE CONCAT('%',#{shValue},'%')
AND a.prReDate BETWEEN #{shStartDate} AND #{shEndDate} AND a.prMoDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.update:
	update product
	set
	prProdName = #{prProdName}
	,prUseNY = #{prUseNY}
	,prProdDesc = #{prProdDesc}
	,prReguPrice = #{prReguPrice}
	,prSalePrice = #{prSalePrice}
	,prInventory = #{prInventory}
	,prDelNY = #{prDelNY}
where 1=1
	and prseq = #{prseq}

mapper.delete:
delete from product
where 1 = 1
	and prseq = #{prseq}
