mapper.sql:
from 
	product a
where 1 = 1
AND prDelNY = #{shDelNY}
AND prUseNY = #{shUseNY}
AND prSort = #{shSort}
AND prSize = #{shSize}
AND prColor = #{shColor}


	AND a.prseq = #{shValue}
	AND a.prProdName LIKE CONCAT('%',#{shValue},'%')
	AND a.prReguPrice LIKE CONCAT('%',#{shValue},'%') 
	AND a.prSalePrice LIKE CONCAT('%',#{shValue},'%') 
	AND a.prInventory LIKE CONCAT('%',#{shValue},'%') 



	AND a.prReDate BETWEEN #{shStartDate} AND #{shEndDate}
	AND a.prMoDate BETWEEN #{shStartDate} AND #{shEndDate}

mapper.insert:
insert into product(
	prProdName
	,prUseNY
	,prSort
	,prSize
	,prColor
	,prProdDesc
	,prReguPrice
	,prSalePrice
	,prInventory
	,prReDate
	,prMoDate
	,prDelNY
)values(
	#{prProdName}
	,#{prUseNY}
	,#{prSort}
	,#{prSize}
	,#{prColor}
	,#{prProdDesc}
	,#{prReguPrice}
	,#{prSalePrice}
	,#{prInventory}
	,now()
	,now()
	,0
)

	SELECT last_Insert_id()

mapper.update:
	update product
	set
	prProdName = #{prProdName}
	,prUseNY = #{prUseNY}
	,prSort = #{prSort}
	,prSize = #{prSize}
	,prColor = #{prColor}
	,prProdDesc = #{prProdDesc}
	,prReguPrice = #{prReguPrice}
	,prSalePrice = #{prSalePrice}
	,prInventory = #{prInventory}
	,prDelNY = #{prDelNY}
where 1=1
	and prseq = #{prseq}

mapper.delete:
delete from product
where 1 = 1
	and prseq = #{prseq}

mapper.insert:
insert into payment(
	pmName
	,pmNum
	,pmZipNum
	,pmAdr
	,pmAdrDt
	,pmEmail
	,pmDelivery
	,pmDelNY
	,Member_memseq
)values(
	#{pmName}
	,#{pmNum}
	,#{pmZipNum}
	,#{pmAdr}
	,#{pmAdrDt}
	,#{pmEmail}
	,#{pmDelivery}
	,0
	,#{Member_memseq}
)

	SELECT last_Insert_id()
